async function getDocuments()
{
var url='/my/service/change_doc';
var formValues= $('#tasdikat_form').serialize();
await $.post(url, formValues, function(data){
$('#print_data_report_form').hide();
console.log("data.....",data)
console.log("nationa;ity...a..",data.data.beneficiary_nationality)
$('#poratl_report_form_archive_number').val(data.data.beneficiary_archive_no != false ?
data.data.beneficiary_archive_no : '')
$('#portal_report_form_person_name').val(data.data.beneficiary_name != false ?
data.data.beneficiary_name : '')
$('#portal_report_form_qid_no').val(data.data.beneficiary_qid != false ?
data.data.beneficiary_qid : '');
$('#portal_report_form_sponsor_name').val(data.data.beneficiary_sponsor != false ?
data.data.beneficiary_sponsor : '');
$('#portal_report_form_mobile').val(data.data.beneficiary_sponsor_mobile != false ?
data.data.beneficiary_sponsor_mobile : '');
$('#portal_report_form_streetlabanon').val(data.data.beneficiary_lebnon_street != false ?
data.data.beneficiary_lebnon_street : '');
$('#portal_report_form_floorno').val(data.data.beneficiary_lebnon_floor != false ?
data.data.beneficiary_lebnon_floor : '');
$('#portal_report_form_moblileno').val(data.data.beneficiary_lebnon_mobile != false ?
data.data.beneficiary_lebnon_mobile : '');
$('#portal_report_form_streetqatar').val(data.data.beneficiary_qatar_street != false ?
data.data.beneficiary_qatar_street : '');
$('#portal_report_form_buildingqatar').val(data.data.beneficiary_qatar_building != false ?
data.data.beneficiary_qatar_building : '');
$('#portal_report_form_floornoqatar').val(data.data.beneficiary_qatar_floor != false ?
data.data.beneficiary_qatar_floor : '');
$('#portal_report_form_mobilenoqatar').val(data.data.beneficiary_qatar_mobile != false ?
data.data.beneficiary_qatar_mobile : '');
$('#portal_service_request_id_new').val(data.data.beneficiary != false ?
data.data.beneficiary : '');
$('#portal_service_request_spouse_id').val(data.data.beneficiary_qid_obj != false ?
data.data.beneficiary_qid_obj : '');
$('#partner_data_report').val(data.data.beneficiary_spose_name)

if(data.data.beneficiary_spose_name != false || data.data.beneficiary_nationality != false
|| data.data.beneficiary_nationality_spouse != false || data.data.beneficiary_lebnon_owned
!= false){
$(`#nationality_form_report_id
option[value=${data.data.beneficiary_nationality}]`).prop("selected", true);
$(`#nationalityspouse_data_report
option[value=${data.data.beneficiary_nationality_spouse}]`).prop("selected", true);
$(`#owned_form_report_id
option[value=${data.data.beneficiary_lebnon_owned}]`).prop("selected", true);
}
console.log(".data.data.beneficiary_nationality_spouse....",data.data.beneficiary_nationality_spouse)
if(data.data.beneficiary_nationality_spouse == 'other'){
console.log("...........if........")
$('.other_nationality_second_div').show();
$('.other_nationality_first_div').hide();
}
else{
console.log("...........else........")
$('.other_nationality_first_div').hide();
$('.other_nationality_second_div').show();
}

});
}

function openModal(e)
{
var url='/my/authorization/document/open/'+e;
formValues = $('#tasdikat_form').serialize();
$.post(url, formValues, function(data)
{
$(".modal-body #service_id").val( data.data.service_id );
$(".modal-body #partner_doc_Id").val( data.data.documentId );
$(".modal-body #document_type").val( data.data.doctype );
$(".modal-body #document_type").prop('disabled', 'disabled');
$(".modal-body #docNo").val( data.data.document_number );
$(".modal-body #issueDate").val( data.data.issue_date );
$(".modal-body #expiryDate").val( data.data.expiry_date );
$(".modal-body #document_owner").val( data.data.document_owner );

$(".modal-body #is_required_issue_date").prop( "checked", data.data.is_required_issue_date
);
$(".modal-body #is_required_expiry_date").prop( "checked", data.data.is_required_expiry_date
);
$(".modal-body #is_required_doc_no").prop( "checked", data.data.is_required_doc_no );
if(data.data.is_required_issue_date==true){
$(".modal-body #issueDatediv").show();
$("#issueDate").attr("required", "required");

}
else
{
$(".modal-body #issueDatediv").hide();
$("#issueDate").removeAttr('required');


}

if(data.data.is_required_expiry_date==true){
$(".modal-body #expiryDatediv").show();
$("#expiryDate").attr("required", "required");

}
else{
$(".modal-body #expiryDatediv").hide();
$("#expiryDate").removeAttr('required');


}

if(data.data.is_required_doc_no==true){
$(".modal-body #docNodiv").show();
$("#docNo").attr("required", "required");

}
else{
$(".modal-body #docNodiv").hide();
$("#docNo").removeAttr('required');


}
$('#docModal').modal('toggle');
});
}
