function validate(isDate)
{
isValid = true;
tmp = isDate.split("/");
isLeap = tmp[2]%4;
if (!/[1-9]{1}/.test(tmp[0]) || /[1-9]{2}/.test(tmp[0]) &&
!/10|11|12/.test(tmp[0])){isValid = false}
else if ((/4|6|9|11/.test(tmp[0])) && tmp[1] > 30){isValid=false}
else if ((/1|3|5|7|8|10|12/.test(tmp[0])) && tmp[1] > 31){isValid=false}
else if ((tmp[0] == 2 && isLeap == 0) && tmp[1] > 29){isValid = false}
else if ((tmp[0] == 2 && isLeap != 0) && tmp[1] > 28){isValid = false}
else if (tmp[2].length != 4 || (!/^19|20/.test(tmp[2]))){isValid = false}
if (isNaN(Date.parse(isDate)) || !isValid){return true}
else { return true }
}

function parseDMY(isDate)
{
tmp = isDate.value;
if(tmp){
tmp = tmp.split("-");
refDate = tmp[1]+"/"+tmp[2]+"/"+tmp[0];
if (!validate(refDate))
{
isDate.value = "";
isDate.focus();
}
}
}

